"use client"

import { useState } from "react"
import { useNavigate } from "react-router-dom"
import "./Dashboard.css"

const Dashboard = ({ token, onLogout }) => {
  const [activeTab, setActiveTab] = useState("home")
  const navigate = useNavigate()

  const getUser = () => {
    try {
      return JSON.parse(atob(token.split(".")[1]))
    } catch {
      return { name: "User", email: "user@example.com" }
    }
  }

  const user = getUser()

  const menuItems = [
    { id: "home", name: "Home", icon: "🏠" },
    { id: "bases", name: "All bases", icon: "📊" },
    { id: "shared", name: "Shared", icon: "👥" },
    { id: "templates", name: "Templates", icon: "📋" },
    { id: "trash", name: "Trash", icon: "🗑️" },
  ]

  const handleProfileClick = () => {
    navigate("/profile")
  }
  // Inside Dashboard component
const handleLogout = () => {
  onLogout();
  window.location.href = "/";
};

  const renderContent = () => {
    if (activeTab === "home") {
      return (
        <div className="home-content">
          <h2>Good morning, {user.name}!</h2>
          <p>Quickly access your recent work below</p>

          <div className="quick-actions">
            <div className="action-card primary">
              <h3>Start with a template</h3>
              <p>Choose from hundreds of templates</p>
              <button>Browse templates</button>
            </div>
            <div className="action-card">
              <h3>Create a base</h3>
              <p>Start from scratch</p>
              <button>Create base</button>
            </div>
          </div>

          <div className="recent-section">
            <h3>Recent bases</h3>
            <div className="empty-state">
              <div className="empty-icon">📊</div>
              <p>You don't have any bases yet. Create your first base!</p>
            </div>
          </div>
        </div>
      )
    }

    return (
      <div className="under-progress">
        <div className="progress-icon">🚧</div>
        <h3>Under Development</h3>
        <p>This feature is coming soon!</p>
      </div>
    )
  }

  return (
    <div className="dashboard">
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="logo">A</div>
          <span>Airtable</span>
        </div>

        <div className="menu">
          {menuItems.map((item) => (
            <div
              key={item.id}
              className={`menu-item ${activeTab === item.id ? "active" : ""}`}
              onClick={() => setActiveTab(item.id)}
            >
              <span className="icon">{item.icon}</span>
              <span>{item.name}</span>
            </div>
          ))}
        </div>

        <div className="user-section" onClick={handleProfileClick}>
          <div className="user-avatar">{user.name.charAt(0)}</div>
          <div className="user-info">
            <div className="user-name">{user.name}</div>
            <div className="user-email">{user.email}</div>
          </div>
        </div>
      </div>

      <div className="main-content">
        <div className="header">
          <h1>{menuItems.find((item) => item.id === activeTab)?.name || "Dashboard"}</h1>
          <div className="header-actions">
            <button className="btn-secondary">Help</button>
            <button className="btn-primary">+ Create</button>
          </div>
        </div>

        <div className="content">{renderContent()}</div>
      </div>
    </div>
  )
}

export default Dashboard
